<script>import classNames from 'classnames';
import { getContext, onMount } from 'svelte';
export let href = undefined;
export let active = false;
export let activeClass = '';
export let normalClass = '';
const group = getContext('group');
const table = getContext('table');
let defaultClass;
$: defaultClass = classNames('block py-2', group ? 'px-3' : 'px-4', 'text-sm font-medium', table || 'border border-gray-300', group || (table ? 'rounded' : 'rounded-lg'), active ? activeClass : normalClass, $$props.class);
</script>

<svelte:element
  this={href ? 'a' : 'button'}
  {href}
  class={defaultClass}
  on:blur
  on:change
  on:click
  on:focus
  on:keydown
  on:keypress
  on:keyup
  on:mouseenter
  on:mouseleave
  on:mouseover>
  <slot />
</svelte:element>
